TriggerOraclev10g.body:
BEGIN
    IF inserting THEN
        :new.name 	    := UPPER(:new.name);
        :new.created_by := nvl(v('APP_USER'), user);
        :new.created_on := current_timestamp;
        :new.changed_on := current_timestamp;
    END IF;
    IF updating THEN
        :new.name 	    := UPPER(:new.name);
        :new.updated_by := nvl(v('APP_USER'), user);
        :new.changed_on := current_timestamp;
    END IF;
END;
